      'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')            }        });        event.preventDefault()        if ($('#login').valid()) {            $('#loading').show();           $('.btn-form').attr('disabled', true);            var email = $("input[name='email']").val();            var password = $("input[name='password']").val();            $.ajax({                type: "POST",                url: "{{ route('login_user') }}",                dataType: "json",                data: {                    email: email,                    password: password                },                success: function (respuesta) {                    if (respuesta.backend) {                        window.location.href = "{{ route('backend') }}";                    }                    if (respuesta.status=='fail') {                        $('#loading').hide();                        toastr.options.timeOut = 2000;                        toastr.error(respuesta.message);                        setTimeout(function () {                            $('.btn-form').attr('disabled', false);                        }, 2000);                    }                }            });        }    });</script>@yield('content')<!-- Bootstrap 4 --><script src="{{ asset('asset/backend/plugins/bootstrap/js/bootstrap.bundle.min.js') }}"></script><!-- AdminLTE App --><script src="{{ asset('asset/backend/js/adminlte.min.js') }}"></script><script type="text/JavaScript" language="javascript" src="{{ asset('asset/backend/plugins/toastr/toastr.min.js') }}"></script></body></html>