rget.files.length; i++) {                    $('.files .icons').append('<div class="icon"><i title="'+ e.target.files[i].name.split('.')[1] + '"></i></div>');                    $('.files .icons').addClass('active');                    $('.files .btn-clear').addClass('active');                }            });            $('.files .btn-clear').on('click', function() {                document.querySelector(".files .input-file").value = "";                $('.files .icons').removeClass('active');                $(".files .icon").remove();                $(this).removeClass("active");            });        });    </script>@endsection