e).attr('src', e.target.result);                }                reader.readAsDataURL(input.files[0]);            }        }        $("#img-upload").change(function () {            var imgControlName = "#imgPreview";            readURL(this, imgControlName);            //$('.btn-remove-image').addClass('active');            $('.image').addClass('active');            $('.image .name').addClass('hide');            $('.image #imgPreview').addClass('show');        });        /*$(".btn-remove-image").click(function(e) {            e.preventDefault();            $("#img-upload").val("");            $('.image').removeClass('active');            $('.image .name').removeClass('hide');            $('.image #imgPreview').removeClass('show');            $('.btn-remove-image').removeClass('active');        });*/        // Editar campos del form        $(".user-data .btn-edit").click(function () {            $(".user-data input").attr("readonly", false);            $(".user-data input").addClass("active");            $(".user-data .btn-edit").addClass("hide");            $(".user-data .btn-save").addClass("show");            $(".user-data form > div:nth-child(1) input").focus();        });        $(".user-data .btn-save").click(function () {            $(".user-data input").attr("readonly", true);            $(".user-data input").removeClass("active");            $(".user-data .btn-edit").removeClass("hide");            $(".user-data .btn-save").removeClass("show");        });    </script>@endsection